<?php	class sidebars	{		public function __construct()		{			ob_start();			ob_clean();		}		public function movetoBuffer($buff = '')		{			if($buff == ''){				$result = ob_get_clean();			}else{				$result[$buff] = ob_get_clean();			}			return $result;		}				public function getMySidebar( $title ){						$this -> __construct();						if( $title == 'blue-sidebar' ){				include 'blue-sidebar.php';			}else{				dynamic_sidebar( $title );			}						$result = $this -> movetoBuffer();						if( strlen( trim($result) ) > 0 ){							$result = "<ul class=\"sidebar ".$title."\">\n" . $result . "</ul>\n";			}						return trim($result);		}		  		public function getClassContent( $sidebars ){			if( in_array('silver' , $sidebars) ){ // 'silver-sidebar'				if( in_array('green' , $sidebars)  ){ // 'green-sidebar'					$result = 'small-content';				}else{					if( in_array('red' , $sidebars) || in_array('orange' , $sidebars) ){ // 'red-sidebar' 'orange-sidebar'						$result = 'small-content';					}else{						$result = 'middle-content';					}				}			}else{				if( in_array('green' , $sidebars) ){ // 'green-sidebar'					$result = 'middle-content';				}else{					if( in_array('red' , $sidebars) || in_array('orange' , $sidebars) ){ // 'red-sidebar' 'orange-sidebar'						$result = 'middle-content';					}else{						$result = 'large-content';					}				}			}						return $result;		}				public function getClassLeftSide( $sidebars ){			if( in_array('silver' , $sidebars) ){ //'silver-sidebar'				if( in_array('l' , $sidebars) ){					$result = 'small-sidebar';				}else{					if( in_array('green' , $sidebars) ){ //'green-sidebar'						$result = 'big-sidebar';					}else{						if( in_array('red' , $sidebars) || in_array('orange' , $sidebars) ){ //  'red-sidebar' 'orange-sidebar'							$result = 'big-sidebar';						}else{							$result = 'small-sidebar';						}					}				}			}else{				if( in_array('green' , $sidebars) ){ // 'green-sidebar'					$result = 'big-sidebar';				}else{					if( in_array('red' , $sidebars) || in_array('orange' , $sidebars) ){ //  'red-sidebar' 'orange-sidebar'						$result = 'big-sidebar';					}else{						if( in_array('blue' , $sidebars) && (!in_array( 'l' , $sidebars )) ) {							$result = 'small-sidebar';						}else{							$result = '';						}					}				}			}						return $result;		}				public function getClassRightSide( $sidebars ){			if( in_array('silver' , $sidebars) ){ // 'silver-sidebar'				if( in_array('r' , $sidebars) ){					$result = 'small-sidebar';				}else{					if( in_array('green' , $sidebars) ){ // 'green-sidebar'						$result = 'big-sidebar';					}else{						if( in_array('red' , $sidebars) || in_array('orange' , $sidebars) ){ //  'red-sidebar' 'orange-sidebar'							$result = 'big-sidebar';						}else{							$result = 'small-sidebar';						}					}				}			}else{				if( in_array('green' , $sidebars) ){ // 'green-sidebar'					$result = 'big-sidebar';				}else{					if( in_array('red' , $sidebars) || in_array('orange' , $sidebars) ){ //  'red-sidebar' 'orange-sidebar'						$result = 'big-sidebar';					}else{						if( in_array('blue' , $sidebars) && (!in_array( 'r' , $sidebars )) ) {							$result = 'small-sidebar';						}else{							$result = '';						}					}				}			}						return $result;		}	}?>